import { Component } from '@angular/core';
import { Router } from '@angular/router'; // Si vous avez besoin de rediriger l'utilisateur
import { NavBarComponent } from '../nav-bar/nav-bar.component';
import { faPrescriptionBottle } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeModule } from '@fortawesome/angular-fontawesome';
import { CommonModule } from '@angular/common';
import { faPlus } from '@fortawesome/free-solid-svg-icons';
import { ApiService } from 'src/services/api.service';
@Component({
  selector: 'app-home-page',
  templateUrl: './home-page.component.html',
  styleUrls: ['./home-page.component.css'],
  standalone:true,
  imports:[NavBarComponent,FontAwesomeModule,CommonModule]
})
export class HomePageComponent {
  faPrescriptionBottle = faPrescriptionBottle;
  faPlus = faPlus;
  hasAnswered: boolean = false;
  message: string = '';
  prescriptions:any = [];
  constructor(private router: Router,private apiService:ApiService) {}
  weekDays: string[] = ['L', 'M', 'M', 'J', 'V', 'S', 'D'];

  // Date actuellement affichée
  displayDate: Date = new Date();
  // Change la date en fonction du défilement
  onScroll(event: any): void {
    const scrollLeft = event.target.scrollLeft;
    const dayWidth = 100;  // Largeur approximative de chaque jour dans le carrousel
    const dayIndex = Math.floor(scrollLeft / dayWidth);

    // Ajouter ou soustraire des jours en fonction du défilement
    const newDate = new Date(this.displayDate);
    newDate.setDate(this.displayDate.getDate() + dayIndex);
    this.displayDate = newDate;
  }

  // Changer la date en augmentant ou diminuant
  changeDate(offset: number): void {
    const newDate = new Date(this.displayDate);
    newDate.setDate(this.displayDate.getDate() + offset);
    this.displayDate = newDate;
  }
  ngAfterViewInit(){
  this.apiService.getPrescription().subscribe(
    (response) => {
     this.prescriptions = response.prescriptions
     console.log(this.prescriptions)
   },
   (error) => {
     console.error('Erreur lors de la vérification des réponses:', error);
     this.message = 'Erreur lors de la vérification de vos réponses.';
   }
 )
}

exportToPDF(){
  console.log("EXPORT PDF")
}
  async ngOnInit() {
    this.displayDate = new Date();
    // Appel initial de la méthode checkNotify
    this.callCheckNotify();
    // Répéter l'appel toutes les 10 secondes
    setInterval(() => {
      this.callCheckNotify();
    }, 10000);

    // Vérification des réponses de l'utilisateur
    this.apiService.checkIfUserHasAnswered().subscribe(
      (response) => {
        this.message = response.message;  // Affiche le message
        this.apiService.handleRedirection(response.valeur);  // Redirige en fonction de la valeur
      },
      (error) => {
        console.error('Erreur lors de la vérification des réponses:', error);
        this.message = 'Erreur lors de la vérification de vos réponses.';
      }
    );


  }

  // Méthode pour appeler checkNotify
  async callCheckNotify() {
    try {
      const response = await this.apiService.checkNotify(); // Convertir l'Observable en Promise
      console.log('Réponse de check-notify', response);
    } catch (error) {
      console.error('Erreur lors de l\'appel à check-notify', error);
    }
  }

  viewPrescriptions() {
    // Redirige vers la page de liste des prescriptions
    this.router.navigate(['/list-prescriptions']); // Assurez-vous que cette route existe
  }

  addPrescription() {
    this.router.navigate(['/add-prescription']); // Assurez-vous que cette route existe
  }
}
<app-nav-bar></app-nav-bar>

<!-- Si prescriptions est vide (prescriptions.length == 0) -->
<div class="container" *ngIf="prescriptions.length == 0">
  <div class="home-page-container">
    <div class="settings-treatment">
      <img class="icon-treatment-1" src="../../../assets/icons/icon-add-treatement-form-2.png"/>
      <h1>Configurer vos traitements</h1>
    </div>

    <div class="title-treatment">
      <img class="icon-label-treatement" src="../../../assets/icons/icon-add-treatement-1.png"/>
      <p>Suivez la prise de tous vos traitements au même endroit.</p>
    </div>

    <div class="title-treatment">
      <img class="icon-label-treatement" src="../../../assets/icons/icon-add-treatement-2.png"/>
      <p>Définissez un programme et recevez des rappels.</p>
    </div>

    <div class="title-treatment">
      <img class="icon-label-treatement" src="../../../assets/icons/icon-add-treatement-3.png"/>
      <p>Les informations relatives à vos traitements sont chiffrées et ne pourront être lues.</p>
    </div>

    <div class="title-treatment">
      <button class="add-treatment" (click)="addPrescription()">Ajouter un traitement</button>
    </div>
  </div>
</div>

<!-- Si prescriptions n'est pas vide (prescriptions.length > 0) -->
<div *ngIf="prescriptions.length > 0" class="container-carroussel-parent">
  <div class="carousel-container">
    <!-- Affichage de la date en haut -->
    <div class="date-display">
      <button (click)="changeDate(-1)">&#8592;</button>
      <p>{{ displayDate | date: 'dd MMMM yyyy' }}</p>
      <button (click)="changeDate(1)">&#8594;</button>
    </div>
  </div>

  <!-- Section pour enregistrer la prescription -->
  <div class="enregistrement-prescription">
    <h2>Enregistrer</h2>
    <p class="status-prescription"></p>
    <button>Traitements au besoin</button>
  </div>

  <!-- Liste des prescriptions -->
  <div class="container-parent-prescription">
    <h2>Vos traitements / prescriptions</h2>
    <div class="container-prescriptions">
      <div class="prescription-item" *ngFor="let prescription of prescriptions">
        <img [src]="prescription.image" alt="Image traitement"/>
        <div class="type">{{ prescription.type }}</div>
        <div class="dosage">{{ prescription.dosage }}</div>
        <div class="recurrence">{{ prescription.recurrence }}</div>
      </div>
    </div>
    <button class="add-treatment" (click)="addPrescription()">Ajouter un traitement</button>
  </div>

  <!-- Détails supplémentaires sur les traitements -->
  <div class="container-parent-prescription-details">
    <h2>A propos des données Traitement</h2>
    <div class="container-details">
      <!-- Détails des traitements -->
    </div>
    <button class="add-treatment" (click)="addPrescription()">Ajouter un traitement</button>
  </div>

  <!-- Export au format PDF -->
  <div class="container-parent-prescription-about">
    <button class="add-treatment" (click)="exportToPDF()">Export au format PDF</button>
  </div>
</div>
