import { CommonModule } from '@angular/common';
import { Component, NgModule, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, ReactiveFormsModule, Validators } from '@angular/forms';
import { NavBarComponent } from '../nav-bar/nav-bar.component';
import { ApiService } from 'src/services/api.service';
import { Medication } from 'src/models/medication';
import { SweetAlert2Module } from '@sweetalert2/ngx-sweetalert2';
import Swal from 'sweetalert2';
import { Router } from '@angular/router';

@Component({
  selector: 'app-prescription-form',
  templateUrl: './prescription-form.component.html',
  styleUrls: ['./prescription-form.component.css'],
  standalone:true,
  imports:[ReactiveFormsModule,CommonModule, NavBarComponent,SweetAlert2Module]
})
export class PrescriptionFormComponent implements OnInit {
  prescriptionForm: FormGroup;
  medications:Medication[];
  stepData: { key: string, value: any }[] = [
    { key: 'nom', value: '' },
    { key: 'traitement', value: '' },
    { key: 'frequence', value: '' },
    { key: 'medicament', value: '' },
    { key: 'dosage', value: '' },
    { key: 'prescriptionDate', value: '' },
    { key: 'prescriptionTime', value: '' }
  ];
  currentStepIndex: number = 0; // Pour suivre l'étape actuelle

  constructor(private router:Router, private fb: FormBuilder, private apiService:ApiService) {
    this.prescriptionForm = this.fb.group({
      medicationId: ['', Validators.required],
      dosage: ['', Validators.required],
      frequence: ['', Validators.required],  // Champ pour la fréquence
      datePrescribed: [new Date().toISOString(), Validators.required], // Date actuelle
      timePrescribed: [new Date().toISOString(), Validators.required], // Heure actuelle
    });
  }




  ngOnInit(): void {
    this.apiService.getMedications().subscribe({
      next: (data) => {
        // S'assurer que les données reçues sont un tableau d'objets Medication
        if (Array.isArray(data)) {
          this.medications = data; // Remplacez le tableau vide par les données reçues
          console.log('Médicaments récupérés :', this.medications);
        } else {
          console.error('Les données récupérées ne sont pas un tableau !');
        }
      },
      error: (err) => {
        console.error('Erreur lors de la récupération des médicaments :', err);
      }
    });
  }

  nextStep(): void {
    if (this.currentStepIndex < 6) {
      this.currentStepIndex++;
    }

    if (this.currentStepIndex === 6) {
      // Afficher un SweetAlert de confirmation avant de valider
      Swal.fire({
        title: 'Êtes-vous sûr ?',
        text: "Êtes-vous sûr de vouloir valider le formulaire ?",
        icon: 'warning',
        showCancelButton: true,
        confirmButtonText: 'Oui, valider',
        cancelButtonText: 'Non, revenir en arrière',
      }).then((result) => {
        if (result.isConfirmed) {
          // Code pour valider le formulaire ou effectuer l'action finale
          this.onSubmit(); // Exemple de méthode pour soumettre le formulaire
        } else {
          // Si l'utilisateur annule, revenir en arrière (ne pas incrémenter l'index)
          this.currentStepIndex--;
        }
      });
    }
  }
  showSuccessAlert() {
    Swal.fire({
      title: 'Succès!',
      text: 'La prescription a été remplie avec succès.',
      icon: 'success',
      confirmButtonText: 'OK'
    }).then((result) => {
      if (result.isConfirmed) {
        // Rediriger vers la page d'accueil (home-page)
        this.router.navigate(['/home-page']);
      }
    });
  }

  onSubmit(): void {
    console.log(this.prescriptionForm)
    if (this.prescriptionForm.valid) {
      const formData = this.prescriptionForm.value;
      console.log(formData);

      // Rechercher le médicament sélectionné par son ID
      const selectedMedication = this.medications.find(
        (med) => med._id === formData.medicationId // Comparer directement les deux comme des chaînes
      );
      // Construire l'objet prescriptionData
      const prescriptionData = {
        medicationId: selectedMedication?._id || null, // Inclure l'ID du médicament
        medicationName: selectedMedication?.name || 'Médicament inconnu',
        frequence: formData.frequence,
        dosage: formData.dosage,
        datePrescribed: formData.datePrescribed,
        timePrescribed: formData.timePrescribed
      };

      this.apiService.postPrescriptions(prescriptionData).subscribe({
        next: (response) => {
          this.showSuccessAlert()
          console.log('Prescription ajoutée avec succès :', response);
        },
        error: (err) => {
          console.error('Erreur lors de l\'envoi de la prescription :', err);
        }
      });

      console.log('Prescription Data:', prescriptionData);

      // Ajouter ici l'appel à une API ou toute autre action pour envoyer les données
      // Exemple : this.apiService.postPrescription(prescriptionData).subscribe(...);
    } else {
      console.log('Le formulaire est invalide.');
    }
  }

}
<app-nav-bar></app-nav-bar>
<div class="container">
    <form class="prescriptionForm" [formGroup]="prescriptionForm" (ngSubmit)="onSubmit()">
      <img class="icon-treatment-1" src="../../../assets/icons/icon-treatment-1.png"/>
      <div class="form_group_1" *ngIf="currentStepIndex === 0">
        <label class="name-treatment" for="Nom-treatement">Nom du traitement</label>
        <input
        type="text"
        class="form-control"
        placeholder="Ajouter le nom de la prescription" />
        <button (click)="nextStep()" type="button" class="next-step">Suivant</button>
      </div>
    <!-- Fréquence -->
    <div class="form-group" *ngIf="currentStepIndex === 1" >
      <label for="frequence">Récurrence</label>
        <select class="select-form" formControlName="frequence">
          <option value="1 fois par jour">1 fois par jour</option>
          <option value="2 fois par jour">2 fois par jour</option>
          <option value="Toutes les 30 secondes">Toutes les 30 secondes</option>
          <option value="Toutes les 10 minutes">Toutes les 10 minutes</option>
          <option value="Toutes les 4 heures">Toutes les 4 heures</option>
          <option value="Toutes les 8 heures">Toutes les 8 heures</option>
          <option value="Toutes les 12 heures">Toutes les 12 heures</option>
        </select>
        <div *ngIf="prescriptionForm.get('frequence')?.invalid && prescriptionForm.get('frequence')?.touched" class="text-danger">
          La fréquence est requise.
        </div>
        <button class="next-step" type="button"  (click)="nextStep()">Suivant</button>
    </div>
      <!-- Liste des médicaments -->
      <div class="form-group" *ngIf="currentStepIndex === 2" >
        <label for="medicationId">Medication</label>
        <select id="medicationId" class="select-form form-control" formControlName="medicationId">
          <option value="" disabled selected>Select a medication</option>
          <option *ngFor="let medication of medications" [value]="medication._id">
            {{ medication.name }}
          </option>
        </select>
        <div *ngIf="prescriptionForm.get('medicationId')?.invalid && prescriptionForm.get('medicationId')?.touched" class="text-danger">
          Vous devez sélectionner un médicament.
        </div>
        <button class="next-step" type="button"  (click)="nextStep()">Suivant</button>
      </div>

      <!-- Dosage -->
      <div class="form-group" *ngIf="currentStepIndex === 3" >
        <label for="dosage">Dosage</label>
        <input
          id="dosage"
          type="text"
          class="form-control"
          formControlName="dosage"
          placeholder="Entrez le dosage"
        />
        <div *ngIf="prescriptionForm.get('dosage')?.invalid && prescriptionForm.get('dosage')?.touched" class="text-danger">
          Le dosage est requis.
        </div>
        <button class="next-step" type="button"  (click)="nextStep()">Suivant</button>
      </div>
  <!-- Date de prescription -->
  <div class="form-group" *ngIf="currentStepIndex === 4" >
    <label for="datePrescribed">Date de prescription</label>
    <input
      id="datePrescribed"
      type="date"
      class="form-control"
      formControlName="datePrescribed"
      [value]="prescriptionForm.get('datePrescribed')?.value"
    />
    <div *ngIf="prescriptionForm.get('datePrescribed')?.invalid && prescriptionForm.get('datePrescribed')?.touched" class="text-danger">
      La date est requise.
    </div>
    <button class="next-step" type="button"  (click)="nextStep()">Suivant</button>
  </div>

  <!-- Heure de prescription -->
  <div class="form-group" *ngIf="currentStepIndex === 5">
  <div class="container-label-arrow">
    <svg fill="#000000" height="800px" width="800px" version="1.1" id="Layer_1" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink" viewBox="0 0 476.213 476.213" xml:space="preserve">
      <polygon points="476.213,223.107 57.427,223.107 151.82,128.713 130.607,107.5 0,238.106 130.607,368.714 151.82,347.5   57.427,253.107 476.213,253.107 "/>
      </svg>
    <label for="timePrescribed">Heure de prescription</label>
  </div>
    <input
      id="timePrescribed"
      type="time"
      class="form-control"
      formControlName="timePrescribed"
      [value]="prescriptionForm.get('timePrescribed')?.value"
    />
    <div *ngIf="prescriptionForm.get('timePrescribed')?.invalid && prescriptionForm.get('timePrescribed')?.touched" class="text-danger">
      L'heure est requise.
    </div>
    <button class="next-step" type="button"  (click)="nextStep()">Suivant</button>

  </div>
      <!-- Bouton de soumission -->

    </form>
</div>
